/* eslint no-unused-vars: 0 */
import React from "react";
import preset from "@rebass/preset";
import { ThemeProvider } from "@emotion/react";
// OR import { ThemeProvider } from 'styled-components'
import { Box, Flex, Heading, Text, Button, Link, Image, Card } from "rebass";
// OR use 'rebass/styled-components'

const theme = {
  ...preset,
};

console.log(theme);

const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <Box>
        <Heading as="h1" mb={4}>
          Rebass Sandbox
        </Heading>
        <Button variant="primary" mr={3} theme={theme}>
          Beep
        </Button>
        <Button variant="outline" theme={theme}>
          Boop
        </Button>
      </Box>
    </ThemeProvider>
  );
};

export default App;
