import React from "react";
import { Button as RebassButton } from "rebass";
import { ThemeProvider } from "@emotion/react";

const Button = ({ children }) => {
  return (
    <ThemeProvider
      theme={{
        colors: {
          background: "black",
          primary: "tomato",
        },
        space: [0, 6, 12, 24, 48],
        fontSizes: [14, 16, 18, 20, 24],
        radii: {
          default: 12,
        },
      }}
    >
      <RebassButton>Beep</RebassButton>
    </ThemeProvider>
  );
};

export default Button;
